import{_ as l,c as i,o as e,ae as t}from"./chunks/framework.Dh67Ezso.js";const c=JSON.parse('{"title":"《认知觉醒》读书笔记","description":"","frontmatter":{},"headers":[],"relativePath":"soft/认知觉醒.md","filePath":"soft/认知觉醒.md","lastUpdated":1741590351000}'),r={name:"soft/认知觉醒.md"};function o(n,a,s,h,d,_){return e(),i("div",null,a[0]||(a[0]=[t('<h1 id="《认知觉醒》读书笔记" tabindex="-1">《认知觉醒》读书笔记 <a class="header-anchor" href="#《认知觉醒》读书笔记" aria-label="Permalink to &quot;《认知觉醒》读书笔记&quot;">​</a></h1><ul><li>每一个人来到这个世界时，其人生观、价值观、世界观全部都是从0开始的，所有的习性、习惯、模式也要从人性的初始状态开始发展，你、我、我们的父辈和孩子都是如此，没有人能直接跨越这个阶段。</li><li>我们对自己的无知使自己看起来就像是一个“醒着的睡着的人”</li></ul><h2 id="第一章-大脑-一切问题的根源" tabindex="-1">第一章 大脑：一切问题的根源 <a class="header-anchor" href="#第一章-大脑-一切问题的根源" aria-label="Permalink to &quot;第一章 大脑：一切问题的根源&quot;">​</a></h2><h3 id="_1-1-大脑的结构" tabindex="-1">1.1 大脑的结构 <a class="header-anchor" href="#_1-1-大脑的结构" aria-label="Permalink to &quot;1.1 大脑的结构&quot;">​</a></h3><p>大脑分为三层：</p><ul><li>本能脑：也叫爬行脑，最古老的大脑，控制本能反应，如心跳、呼吸等。</li><li>情绪脑：也叫猴子脑，控制情绪反应，如愤怒、恐惧等。</li><li>理智脑：也叫思考脑，控制理性思考，如逻辑推理、决策等。</li></ul><p>心得体会：</p><ul><li>要跟着理智脑做事，理智脑才会给理智脑真正的快乐</li><li>成长就是克服天性的过程</li><li>习惯是可以自我巩固的-越用越强，越强越用</li><li>要想从既有的习惯中跳出来，最好的方法不是自制力，而是靠知识</li></ul><h3 id="_1-2-焦虑的根源" tabindex="-1">1.2 焦虑的根源 <a class="header-anchor" href="#_1-2-焦虑的根源" aria-label="Permalink to &quot;1.2 焦虑的根源&quot;">​</a></h3><p>不行动，没有踏实的行动</p><p>五种焦虑：</p><ul><li>完成焦虑</li><li>定位焦虑 当我们在刚入门的时候去看那些已经在该领域取得成就的人，会感到焦虑</li><li>选择焦虑</li><li>环境焦虑</li><li>难度焦虑</li></ul><h2 id="第二章-潜意识-生命留给我们的彩蛋" tabindex="-1">第二章 潜意识：生命留给我们的彩蛋 <a class="header-anchor" href="#第二章-潜意识-生命留给我们的彩蛋" aria-label="Permalink to &quot;第二章 潜意识：生命留给我们的彩蛋&quot;">​</a></h2><h3 id="_2-1-潜意识的力量" tabindex="-1">2.1 潜意识的力量 <a class="header-anchor" href="#_2-1-潜意识的力量" aria-label="Permalink to &quot;2.1 潜意识的力量&quot;">​</a></h3><p>潜意识的力量：</p><ul><li>潜意识是习惯的温床</li><li>潜意识是情绪的源泉</li><li>潜意识是智慧的源泉</li></ul>',16)]))}const p=l(r,[["render",o]]);export{c as __pageData,p as default};
